import React, { useState } from "react";
import { addJournalEntry } from "../api";

export default function JournalForm({ onNewEntry }) {
  const [text, setText] = useState("");
  const [status, setStatus] = useState("idle");
  const [sentiment, setSentiment] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!text.trim()) return;

    setStatus("analyzing");
    try {
      const res = await addJournalEntry(text);
      setSentiment(res.sentiment);
      onNewEntry({ text, sentiment: res.sentiment });
      setStatus("done");
      setText("");
    } catch (error) {
      console.error("‚ùå Error adding entry:", error);
      alert("Error saving entry. Check backend connection!");
      setStatus("idle");
    }
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="w-full max-w-lg bg-white shadow-lg rounded-2xl p-6 flex flex-col items-center space-y-4"
    >
      <textarea
        value={text}
        onChange={(e) => setText(e.target.value)}
        placeholder="Write your thoughts here..."
        className="w-full h-32 p-3 border rounded-xl focus:ring-2 focus:ring-indigo-400 outline-none resize-none"
      />

      <button
        type="submit"
        disabled={status === "analyzing"}
        className={`px-6 py-2 rounded-lg text-white font-semibold transition ${
          status === "analyzing"
            ? "bg-gray-400 cursor-not-allowed"
            : "bg-indigo-600 hover:bg-indigo-700"
        }`}
      >
        {status === "analyzing" ? "Analyzing..." : "Save Entry"}
      </button>

      {sentiment && (
        <p className="text-sm text-gray-700">
          üß† Detected Emotion:{" "}
          <span className="font-bold">
            {sentiment === "Happy"
              ? "üòä Happy"
              : sentiment === "Sad"
              ? "üò¢ Sad"
              : "üòê Neutral"}
          </span>
        </p>
      )}
    </form>
  );
}
