import React, { useState, useRef } from "react";
import { API } from "../api";

export default function JournalForm({ onNewEntry }) {
  const [text, setText] = useState("");
  const [loading, setLoading] = useState(false);
  const [listening, setListening] = useState(false);
  const recognitionRef = useRef(null);

  const startListening = () => {
    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      alert("Speech recognition not supported in this browser!");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = "en-US";
    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setText((prev) => (prev ? prev + " " : "") + transcript);
    };
    recognition.onend = () => setListening(false);
    recognitionRef.current = recognition;
    recognition.start();
    setListening(true);
  };

  const stopListening = () => {
    recognitionRef.current?.stop();
    setListening(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!text.trim()) return;
    setLoading(true);
    try {
      const res = await API.post("/add_entry", { text });
      onNewEntry(res.data.entry);
      setText("");
    } catch {
      alert("Error saving entry");
    } finally {
      setLoading(false);
    }
  };

  // âœ… Return must be inside the component function
  return (
    <form
      onSubmit={handleSubmit}
      className="p-6 bg-white/80 backdrop-blur-lg shadow-xl rounded-2xl w-full max-w-2xl border border-indigo-100"
    >
      <textarea
        className="border w-full p-4 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-400 bg-white/60"
        rows="4"
        placeholder="Write or speak your thoughts..."
        value={text}
        onChange={(e) => setText(e.target.value)}
      />
      <div className="flex justify-between mt-4">
        <button
          type="button"
          onClick={listening ? stopListening : startListening}
          className={`px-5 py-2.5 rounded-lg text-white font-semibold shadow-md ${
            listening
              ? "bg-red-500 hover:bg-red-600"
              : "bg-green-500 hover:bg-green-600"
          }`}
        >
          {listening ? "ðŸ›‘ Stop" : "ðŸŽ¤ Speak"}
        </button>

        <button
          type="submit"
          disabled={loading}
          className="px-5 py-2.5 bg-indigo-600 text-white font-semibold rounded-lg shadow-md hover:bg-indigo-700 disabled:opacity-50"
        >
          {loading ? "Saving..." : "ðŸ’¾ Save Entry"}
        </button>
      </div>
    </form>
  );
}
