import React, { useState, useRef } from "react";
import { addJournalEntry } from "../api";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

export default function JournalForm({ onNewEntry }) {
  const [text, setText] = useState("");
  const [loading, setLoading] = useState(false);
  const [listening, setListening] = useState(false);
  const recognitionRef = useRef(null);

  // üéôÔ∏è Start voice recording
  const startListening = () => {
    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) {
      toast.error("üé§ Speech recognition not supported in this browser!");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = "en-US";
    recognition.continuous = false;

    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setText((prev) => (prev ? prev + " " : "") + transcript);
    };

    recognition.onerror = (err) => {
      console.error("Speech error:", err);
      toast.error("Error with microphone!");
      setListening(false);
    };

    recognition.onend = () => setListening(false);
    recognitionRef.current = recognition;
    recognition.start();
    setListening(true);
    toast.info("üéß Listening...");
  };

  // üõë Stop listening
  const stopListening = () => {
    recognitionRef.current?.stop();
    setListening(false);
    toast.info("üõë Stopped listening");
  };

  // üíæ Handle Save Entry
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!text.trim()) {
      toast.warning("‚úçÔ∏è Please write or speak something first!");
      return;
    }

    // Temporary instant entry to show on UI
    const tempEntry = {
      text,
      sentiment: "Analyzing...",
      date: new Date().toISOString().split("T")[0],
    };

    onNewEntry(tempEntry); // ‚úÖ adds instantly to UI

    setText("");
    setLoading(true);

    try {
      const result = await addJournalEntry(text); // ‚úÖ uses imported function
      toast.success("‚úÖ Entry saved successfully!");

      // Update emotion once backend responds
      onNewEntry({
        text,
        sentiment: result.sentiment,
        date: new Date().toISOString().split("T")[0],
      });
    } catch (error) {
      console.error(error);
      toast.error("‚ùå Error saving entry. Check backend connection!");
    } finally {
      setLoading(false);
    }
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="p-6 bg-white/80 backdrop-blur-lg rounded-2xl w-full max-w-2xl border border-indigo-100 shadow-xl card-glow"
    >
      {/* Text Input Area */}
      <textarea
        className="border w-full p-4 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-400 bg-white/70 placeholder-gray-400"
        rows="4"
        placeholder="Write or speak your thoughts..."
        value={text}
        onChange={(e) => setText(e.target.value)}
      />

      {/* Buttons */}
      <div className="flex justify-between items-center mt-4">
        {/* üé§ Voice Input Button */}
        <button
          type="button"
          onClick={listening ? stopListening : startListening}
          className={`px-5 py-2.5 rounded-lg text-white font-semibold shadow-md flex items-center gap-2 transition ${
            listening
              ? "bg-red-500 hover:bg-red-600"
              : "bg-green-500 hover:bg-green-600"
          }`}
        >
          {listening ? "üõë Stop" : "üé§ Speak"}
        </button>

        {/* üíæ Save Entry Button */}
        <button
          type="submit"
          disabled={loading}
          className="px-6 py-2.5 bg-gradient-to-r from-indigo-600 to-pink-600 text-white font-semibold rounded-lg shadow-md hover:opacity-90 disabled:opacity-60"
        >
          {loading ? (
            <span className="animate-pulse">üí´ Saving...</span>
          ) : (
            "üíæ Save Entry"
          )}
        </button>
      </div>

      {/* Toast Notifications */}
      <ToastContainer position="top-right" autoClose={1500} theme="light" />
    </form>
  );
}
