import React, { useEffect, useState } from "react";
import { BarChart, Bar, XAxis, YAxis, Tooltip, Legend, ResponsiveContainer } from "recharts";
import { getEmotionTrends } from "../api";

export default function EmotionTrends() {
  const [data, setData] = useState([]);

  useEffect(() => {
    (async () => {
      try {
        const trendData = await getEmotionTrends();
        setData(trendData);
      } catch (error) {
        console.error("Error fetching trend data:", error);
      }
    })();
  }, []);

  return (
    <div className="mt-10 w-full max-w-2xl bg-white/80 rounded-2xl shadow-md p-6 border border-indigo-100">
      <h2 className="text-xl font-semibold text-indigo-600 mb-4">ðŸ“… Mood Trend (by Day)</h2>
      {data.length === 0 ? (
        <p className="text-gray-500 text-center italic">No data yet â€” add some entries!</p>
      ) : (
        <ResponsiveContainer width="100%" height={300}>
          <BarChart data={data}>
            <XAxis dataKey="date" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey="Happy" fill="#4ade80" />
            <Bar dataKey="Sad" fill="#f87171" />
            <Bar dataKey="Neutral" fill="#a1a1aa" />
          </BarChart>
        </ResponsiveContainer>
      )}
    </div>
  );
}
