import React from "react";
import { Line } from "react-chartjs-2";
import {
  Chart,
  LineElement,
  CategoryScale,
  LinearScale,
  PointElement,
  Tooltip,
  Legend,
} from "chart.js";

Chart.register(LineElement, CategoryScale, LinearScale, PointElement, Tooltip, Legend);

export default function EmotionChart({ entries }) {
  const data = {
    labels: entries.map((e) => e.date),
    datasets: [
      {
        label: "Emotion Trend",
        data: entries.map((e) =>
          e.emotion === "Happy" ? 1 : e.emotion === "Sad" ? -1 : 0
        ),
        borderWidth: 3,
        tension: 0.4,
        fill: true,
        backgroundColor: "rgba(236, 72, 153, 0.15)",
        borderColor: "rgba(147, 51, 234, 1)",
        pointBackgroundColor: "#ec4899",
      },
    ],
  };

  return (
    <div className="w-full max-w-2xl bg-white/80 backdrop-blur-lg rounded-2xl shadow-lg p-6 border border-purple-100 card-glow">
      <h2 className="text-xl font-semibold mb-3 text-indigo-600 flex items-center gap-2">
        ðŸ“ˆ Emotion Trend
      </h2>
      <Line data={data} />
    </div>
  );
}
